mapper.select:
SELECT sb.name as senderBank, u.name as userName, r.sender_account as senderAccount, CONCAT(c.country_name, ' - ', c.code_name, ' (', c.code, ')') as currency, r.receiver_bank as receiverBank, r.receiver_account as receiverAccount, rb.name as receiverBankName, r.receiver_name as receiverName, r.amount, CASE WHEN r.status = 'COMPLETED' THEN '완료' WHEN r.status = 'WAITTING' THEN '대기' END AS status, r.created_at as createdAt FROM remittance r LEFT JOIN bank sb ON r.sender_bank = sb.bank_code LEFT JOIN bank rb ON r.receiver_bank = rb.bank_code LEFT JOIN country c ON r.currency = c.code LEFT JOIN user u ON r.user_id = u.id
AND u.name LIKE CONCAT('%', #{userName}, '%')
AND r.receiver_name LIKE CONCAT('%', #{receiverName}, '%')
AND r.currency = #{currency}
AND r.status = #{status}
AND r.amount >= #{minAmount}
AND r.amount <= #{maxAmount}
AND DATE(r.created_at) >= #{startDate}
AND DATE(r.created_at) <= #{endDate}
ORDER BY
r.created_at ASC
r.amount DESC
r.amount ASC
r.created_at DESC
LIMIT #{size} OFFSET #{page}


SELECT COUNT(*)
FROM remittance r
LEFT JOIN bank sb ON r.sender_bank = sb.bank_code
LEFT JOIN bank rb ON r.receiver_bank = rb.bank_code
LEFT JOIN country c ON r.currency = c.code
LEFT JOIN user u ON r.user_id = u.id


        AND u.name LIKE CONCAT('%', #{userName}, '%')


        AND r.receiver_name LIKE CONCAT('%', #{receiverName}, '%')


        AND r.currency = #{currency}


        AND r.status = #{status}


        AND r.amount >= #{minAmount}


        AND r.amount <= #{maxAmount}


        AND DATE(r.created_at) >= #{startDate}


        AND DATE(r.created_at) <= #{endDate}


SELECT EXISTS(
    SELECT 1 FROM user_remittance_limit 
    WHERE user_id = #{userId}
)
